import { useState, useEffect, useRef } from "react";
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer, Area, AreaChart } from "recharts";

function LiveTradingChart() {
  const [liveData, setLiveData] = useState([]);
  const [isConnected, setIsConnected] = useState(false);
  const [selectedSymbol, setSelectedSymbol] = useState("EURUSD");
  const wsRef = useRef(null);

  useEffect(() => {
    // Simulate live trading data (in real app, this would connect to actual trading API)
    const generateLivePrice = () => {
      const now = new Date();
      const basePrice = 1.0850;
      const variation = (Math.random() - 0.5) * 0.002;
      const price = basePrice + variation;

      return {
        time: now.toLocaleTimeString(),
        price: parseFloat(price.toFixed(5)),
        bid: parseFloat((price - 0.0001).toFixed(5)),
        ask: parseFloat((price + 0.0001).toFixed(5)),
        volume: Math.floor(Math.random() * 1000000) + 500000
      };
    };

    // Start live data simulation
    const interval = setInterval(() => {
      const newDataPoint = generateLivePrice();
      setLiveData(prev => {
        const updated = [...prev, newDataPoint];
        // Keep only last 50 data points
        return updated.slice(-50);
      });
      setIsConnected(true);
    }, 1000);

    return () => clearInterval(interval);
  }, [selectedSymbol]);

  const formatPrice = (value) => {
    return value.toFixed(5);
  };

  const formatVolume = (value) => {
    if (value >= 1000000) return `${(value / 1000000).toFixed(1)}M`;
    if (value >= 1000) return `${(value / 1000).toFixed(1)}K`;
    return value.toString();
  };

  const latestData = liveData[liveData.length - 1] || { price: 1.0850, bid: 1.0849, ask: 1.0851, volume: 750000 };

  return (
    <div className="page">
      <div className="card" style={{ marginBottom: 20 }}>
        <div className="card-header">
          <div>
            <div className="card-title">ðŸ“ˆ Live Trading View</div>
            <div className="card-sub">Real-time price and volume data</div>
          </div>
          <div style={{ display: "flex", alignItems: "center", gap: "12px" }}>
            <select 
              value={selectedSymbol} 
              onChange={(e) => setSelectedSymbol(e.target.value)}
              style={{
                padding: "6px 12px",
                borderRadius: "6px",
                border: "1px solid var(--border)",
                background: "var(--surface)",
                color: "var(--text)",
                fontSize: "13px"
              }}
            >
              <option value="EURUSD">EUR/USD</option>
              <option value="GBPUSD">GBP/USD</option>
              <option value="USDJPY">USD/JPY</option>
              <option value="AUDUSD">AUD/USD</option>
            </select>
            <div style={{ 
              display: "flex", 
              alignItems: "center", 
              gap: "6px",
              padding: "4px 12px",
              borderRadius: "6px",
              background: isConnected ? "rgba(0,212,170,0.1)" : "rgba(239,68,68,0.1)",
              border: `1px solid ${isConnected ? "var(--accent)" : "var(--red)"}`
            }}>
              <div style={{ 
                width: "8px", 
                height: "8px", 
                borderRadius: "50%", 
                background: isConnected ? "var(--accent)" : "var(--red)" 
              }} />
              <span style={{ fontSize: "12px", color: isConnected ? "var(--accent)" : "var(--red)" }}>
                {isConnected ? "Connected" : "Disconnected"}
              </span>
            </div>
          </div>
        </div>

        {/* Price Display */}
        <div style={{ 
          display: "grid", 
          gridTemplateColumns: "repeat(4, 1fr)", 
          gap: "16px", 
          marginBottom: "20px",
          padding: "16px",
          background: "var(--surface)",
          borderRadius: "8px"
        }}>
          <div>
            <div style={{ fontSize: "11px", color: "var(--muted)", marginBottom: "4px" }}>Current Price</div>
            <div style={{ fontSize: "18px", fontWeight: "600", color: "var(--text)" }}>
              {formatPrice(latestData.price)}
            </div>
          </div>
          <div>
            <div style={{ fontSize: "11px", color: "var(--muted)", marginBottom: "4px" }}>Bid</div>
            <div style={{ fontSize: "16px", fontWeight: "500", color: "var(--red)" }}>
              {formatPrice(latestData.bid)}
            </div>
          </div>
          <div>
            <div style={{ fontSize: "11px", color: "var(--muted)", marginBottom: "4px" }}>Ask</div>
            <div style={{ fontSize: "16px", fontWeight: "500", color: "var(--accent)" }}>
              {formatPrice(latestData.ask)}
            </div>
          </div>
          <div>
            <div style={{ fontSize: "11px", color: "var(--muted)", marginBottom: "4px" }}>Volume</div>
            <div style={{ fontSize: "16px", fontWeight: "500", color: "var(--text)" }}>
              {formatVolume(latestData.volume)}
            </div>
          </div>
        </div>

        {/* Live Price Chart */}
        <div style={{ height: "300px", marginBottom: "20px" }}>
          <ResponsiveContainer width="100%" height="100%">
            <AreaChart data={liveData}>
              <defs>
                <linearGradient id="colorPrice" x1="0" y1="0" x2="0" y2="1">
                  <stop offset="5%" stopColor="var(--accent)" stopOpacity={0.8}/>
                  <stop offset="95%" stopColor="var(--accent)" stopOpacity={0.1}/>
                </linearGradient>
              </defs>
              <CartesianGrid strokeDasharray="3 3" stroke="rgba(255,255,255,0.04)" />
              <XAxis 
                dataKey="time" 
                tick={{ fill: "#64748b", fontSize: 10 }} 
                axisLine={false} 
                tickLine={false}
              />
              <YAxis 
                domain={['dataMin - 0.001', 'dataMax + 0.001']}
                tick={{ fill: "#64748b", fontSize: 10 }} 
                axisLine={false} 
                tickLine={false}
                tickFormatter={formatPrice}
              />
              <Tooltip 
                contentStyle={{ 
                  background: "var(--surface)", 
                  border: "1px solid var(--border)",
                  borderRadius: "6px"
                }}
                labelStyle={{ color: "var(--text)" }}
                formatter={(value, name) => {
                  if (name === 'price') return [formatPrice(value), 'Price'];
                  return [value, name];
                }}
              />
              <Area 
                type="monotone" 
                dataKey="price" 
                stroke="var(--accent)" 
                strokeWidth={2}
                fillOpacity={1} 
                fill="url(#colorPrice)" 
              />
            </AreaChart>
          </ResponsiveContainer>
        </div>

        {/* Volume Chart */}
        <div style={{ height: "150px" }}>
          <ResponsiveContainer width="100%" height="100%">
            <AreaChart data={liveData}>
              <CartesianGrid strokeDasharray="3 3" stroke="rgba(255,255,255,0.04)" />
              <XAxis 
                dataKey="time" 
                tick={{ fill: "#64748b", fontSize: 10 }} 
                axisLine={false} 
                tickLine={false}
              />
              <YAxis 
                tick={{ fill: "#64748b", fontSize: 10 }} 
                axisLine={false} 
                tickLine={false}
                tickFormatter={formatVolume}
              />
              <Tooltip 
                contentStyle={{ 
                  background: "var(--surface)", 
                  border: "1px solid var(--border)",
                  borderRadius: "6px"
                }}
                labelStyle={{ color: "var(--text)" }}
                formatter={(value) => [formatVolume(value), 'Volume']}
              />
              <Area 
                type="monotone" 
                dataKey="volume" 
                stroke="var(--accent2)" 
                strokeWidth={1}
                fillOpacity={0.3} 
                fill="var(--accent2)" 
              />
            </AreaChart>
          </ResponsiveContainer>
        </div>
      </div>
    </div>
  );
}

export default LiveTradingChart;
