import api from './api';

export const aiService = {
  getInsights: async () => {
    try {
      const response = await api.get('/ai/insights');
      return response.data.data;
    } catch (error) {
      return {
        insights: [
          { type: "warning", icon: "âš ï¸", title: "Revenge Trading Detected", desc: "You placed 3 trades within 15 minutes after a loss on Tuesday. Win rate on revenge trades: 22%." },
          { type: "success", icon: "âœ…", title: "London Overlap Mastery", desc: "Your win rate during London/NY overlap is 81%. Consider focusing more sessions here." },
          { type: "info", icon: "ðŸ§ ", title: "Emotional Pattern", desc: "Trades tagged 'Anxious' have a 34% win rate vs 74% when 'Confident'. Journal before every entry." },
          { type: "warning", icon: "ðŸ“Š", title: "Risk Inconsistency", desc: "Position sizing variance is 40% week-over-week. Standardize to 1% risk per trade." },
        ]
      };
    }
  },

  getTradeAnalysis: async (tradeId) => {
    try {
      const response = await api.get(`/ai/trade-analysis/${tradeId}`);
      return response.data.data;
    } catch (error) {
      return {
        qualityScore: 8.4,
        riskDisciplineRating: 7.8,
        emotionalDisciplineRating: 9.1,
        mistakeDetected: false,
        behavioralFlags: [],
        coachingFeedback: "Excellent trade execution with proper risk management.",
        suggestedImprovement: "Consider scaling in on strong trends."
      };
    }
  },

  getPatternDetection: async () => {
    try {
      const response = await api.get('/ai/patterns');
      return response.data.data;
    } catch (error) {
      return {
        patterns: [
          { name: "FOMO Trading", frequency: "High", impact: "Negative", description: "Tendency to chase price movements" },
          { name: "Breakout Trading", frequency: "Medium", impact: "Positive", description: "Strong performance on momentum breaks" }
        ]
      };
    }
  },

  getEmotionalAnalysis: async () => {
    try {
      const response = await api.get('/ai/emotional-analysis');
      return response.data.data;
    } catch (error) {
      return {
        emotions: [
          { name: "Confident", value: 35, color: "#00d4aa" },
          { name: "Neutral", value: 28, color: "#6366f1" },
          { name: "Anxious", value: 18, color: "#f59e0b" },
          { name: "FOMO", value: 12, color: "#ef4444" },
          { name: "Revenge", value: 7, color: "#dc2626" },
        ]
      };
    }
  },

  getRecommendations: async () => {
    try {
      const response = await api.get('/ai/recommendations');
      return response.data.data;
    } catch (error) {
      return {
        recommendations: [
          "Implement mandatory 30-minute cooling period after losses",
          "Focus on London/NY overlap sessions for higher win rates",
          "Standardize position sizing to 1% risk per trade",
          "Journal emotions before each trade entry"
        ]
      };
    }
  },

  generateReport: async (type) => {
    try {
      const response = await api.post('/ai/generate-report', { type });
      return response.data.data;
    } catch (error) {
      return {
        type,
        generated: true,
        content: `AI-generated ${type} report with detailed analysis and recommendations.`
      };
    }
  }
};
