import { useState, useEffect } from "react";
import { tradeService } from "../services/tradeService";
import { aiService } from "../services/aiService";
import "../styles/styles.css";

function Journal({ onAddTrade }) {
  const [trades, setTrades] = useState([]);
  const [loading, setLoading] = useState(true);
  const [filter, setFilter] = useState("ALL");
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchTrades = async () => {
      try {
        setLoading(true);
        const response = await tradeService.getAllTrades();
        setTrades(response.data || response.trades || []);
      } catch (err) {
        console.error('Failed to fetch trades:', err);
        setError('Failed to load trades');
        setTrades([]);
      } finally {
        setLoading(false);
      }
    };

    fetchTrades();
  }, []);

  const analyzeTrade = async (tradeId) => {
    try {
      const analysis = await aiService.getTradeAnalysis(tradeId);
      // Update the trade with AI analysis
      setTrades(prevTrades => 
        prevTrades.map(trade => 
          trade.id === tradeId || trade._id === tradeId 
            ? { ...trade, aiAnalysis: analysis, aiScore: analysis.qualityScore }
            : trade
        )
      );
    } catch (err) {
      console.error('Failed to analyze trade:', err);
    }
  };

  const filtered = filter === "ALL" ? trades : trades.filter(t => t.status === filter);

  if (loading) {
    return (
      <div className="page">
        <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '400px' }}>
          <div>Loading trades...</div>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="page">
        <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '400px' }}>
          <div>{error}</div>
        </div>
      </div>
    );
  }

  return (
    <div className="page">
      <div style={{ display: "flex", alignItems: "center", justifyContent: "space-between", marginBottom: 20 }}>
        <div className="tabs">
          {["ALL", "WIN", "LOSS"].map(f => (
            <div key={f} className={`tab ${filter === f ? "active" : ""}`} onClick={() => setFilter(f)}>{f}</div>
          ))}
        </div>
        <button className="btn btn-primary" onClick={onAddTrade}>+ Log Trade</button>
      </div>

      <div className="card">
        <div className="table-wrap">
          <table>
            <thead>
              <tr>
                <th>Symbol</th><th>Dir</th><th>Entry</th><th>Exit</th>
                <th>PnL</th><th>R:R</th><th>Strategy</th><th>Emotion</th>
                <th>AI Score</th><th>Mistake</th><th>Actions</th><th>Date</th>
              </tr>
            </thead>
            <tbody>
              {filtered.map(t => (
                <tr key={t.id}>
                  <td><span className="symbol">{t.symbol}</span></td>
                  <td><span className={t.dir === "LONG" ? "dir-long" : "dir-short"}>{t.dir}</span></td>
                  <td style={{ fontFamily: "var(--font-mono)", fontSize: 12 }}>{t.entry}</td>
                  <td style={{ fontFamily: "var(--font-mono)", fontSize: 12 }}>{t.exit}</td>
                  <td><span className={t.pnl >= 0 ? "pnl-pos" : "pnl-neg"}>{t.pnl >= 0 ? "+" : ""}${t.pnl}</span></td>
                  <td style={{ fontFamily: "var(--font-mono)", fontSize: 12, color: parseFloat(t.rr) > 0 ? "var(--accent)" : "var(--red)" }}>{t.rr}</td>
                  <td><span style={{ fontSize: 12, color: "var(--muted)" }}>{t.strategy}</span></td>
                  <td><span style={{ fontSize: 12 }}>{t.emotion}</span></td>
                  <td>
                    <div className="score-bar">
                      <span className={`ai-score ${t.aiScore >= 7 ? "ai-high" : t.aiScore >= 5 ? "ai-mid" : "ai-low"}`}>{t.aiScore}</span>
                      <div className="score-bg"><div className="score-fill" style={{ width: `${t.aiScore * 10}%` }} /></div>
                    </div>
                  </td>
                  <td>{t.mistake ? <span className="mistake-tag">{t.mistake}</span> : <span style={{ color: "var(--muted)", fontSize: 12 }}>â€”</span>}</td>
                  <td>
                    <button 
                      className="btn btn-ghost" 
                      onClick={() => analyzeTrade(t.id || t._id)}
                      style={{ fontSize: "11px", padding: "4px 8px" }}
                    >
                      ðŸ¤– Analyze
                    </button>
                  </td>
                  <td style={{ fontSize: 12, color: "var(--muted)" }}>{t.date}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
}

export default Journal;
