import api from './api';

export const analyticsService = {
  getDashboardStats: async () => {
    try {
      const response = await api.get('/analytics/summary');
      return response.data.data;
    } catch (error) {
      // Return mock data if API fails
      return {
        totalPnL: 2800,
        totalPnLChange: 18.4,
        winRate: 68.2,
        winRateChange: 3.1,
        avgRiskReward: 2.31,
        avgRiskRewardChange: 0.4,
        drawdown: -4.2,
        drawdownChange: -1.1,
        weeklyPnL: [
          { day: "Mon", pnl: 320, trades: 4 }, 
          { day: "Tue", pnl: -140, trades: 3 },
          { day: "Wed", pnl: 580, trades: 6 }, 
          { day: "Thu", pnl: 210, trades: 2 },
          { day: "Fri", pnl: 430, trades: 5 },
        ]
      };
    }
  },

  getEquityCurve: async (period = '1M') => {
    try {
      const response = await api.get(`/analytics/equity-curve?period=${period}`);
      return response.data.data;
    } catch (error) {
      return [
        { date: "Jan 1", pnl: 0 }, { date: "Jan 8", pnl: 420 }, { date: "Jan 15", pnl: 310 },
        { date: "Jan 22", pnl: 890 }, { date: "Jan 29", pnl: 760 }, { date: "Feb 5", pnl: 1240 },
        { date: "Feb 12", pnl: 1100 }, { date: "Feb 19", pnl: 1680 }, { date: "Feb 26", pnl: 2100 },
        { date: "Mar 5", pnl: 1900 }, { date: "Mar 12", pnl: 2450 }, { date: "Mar 19", pnl: 2800 },
      ];
    }
  },

  getPerformanceMetrics: async () => {
    try {
      const response = await api.get('/analytics/summary');
      return response.data.data;
    } catch (error) {
      return {
        totalTrades: 45,
        winRate: 68.2,
        profitFactor: 2.1,
        avgWin: 156,
        avgLoss: -74,
        largestWin: 1680,
        largestLoss: -650
      };
    }
  },

  getSessionStats: async () => {
    try {
      const response = await api.get('/analytics/sessions');
      return response.data.data;
    } catch (error) {
      return [
        { session: "London", winRate: 68, pnl: 1240 },
        { session: "NY", winRate: 72, pnl: 1890 },
        { session: "Asia", winRate: 54, pnl: 310 },
        { session: "Overlap", winRate: 81, pnl: 2100 },
      ];
    }
  },

  getEmotionAnalysis: async () => {
    try {
      const response = await api.get('/analytics/emotions');
      return response.data.data.map(emotion => ({
        name: emotion._id,
        value: emotion.trades,
        color: emotion._id === 'Confident' ? "#00d4aa" : 
               emotion._id === 'Neutral' ? "#6366f1" : 
               emotion._id === 'Anxious' ? "#f59e0b" : 
               emotion._id === 'FOMO' ? "#ef4444" : "#dc2626"
      }));
    } catch (error) {
      return [
        { name: "Confident", value: 35, color: "#00d4aa" },
        { name: "Neutral", value: 28, color: "#6366f1" },
        { name: "Anxious", value: 18, color: "#f59e0b" },
        { name: "FOMO", value: 12, color: "#ef4444" },
        { name: "Revenge", value: 7, color: "#dc2626" },
      ];
    }
  },

  getWinRateByStrategy: async () => {
    try {
      const response = await api.get('/analytics/strategies');
      return response.data.data;
    } catch (error) {
      return [
        { strategy: "Breakout", winRate: 78, trades: 12 },
        { strategy: "Reversal", winRate: 62, trades: 8 },
        { strategy: "Trend Follow", winRate: 71, trades: 14 }
      ];
    }
  },

  getRiskMetrics: async () => {
    try {
      const response = await api.get('/analytics/summary');
      return response.data.data;
    } catch (error) {
      return {
        currentDrawdown: -4.2,
        maxDrawdown: -8.7,
        sharpeRatio: 1.84,
        avgRiskPerTrade: 1.2
      };
    }
  },

  getCalendarHeatmap: async (month, year) => {
    try {
      const response = await api.get(`/analytics/daily-pnl?month=${month}&year=${year}`);
      return response.data.data;
    } catch (error) {
      return [];
    }
  }
};
